import React, { useState, useRef, useEffect } from "react";
import { gsap, Expo } from "gsap";
import Prompt from "./components/prompt";
import CircularProgress from "@mui/material/CircularProgress";
import SpeedDial from "@mui/material/SpeedDial";
import Box from "@mui/material/Box";
import SpeedDialIcon from "@mui/material/SpeedDialIcon";
import SpeedDialAction from "@mui/material/SpeedDialAction";
import SaveIcon from "@mui/icons-material/Save";
import ShareIcon from "@mui/icons-material/Share";
import CustomizedSlider from "./components/upvoteslider";
import Button from '@mui/material/Button';

export default function Form() {
  const backendUrl = "http://localhost:5000" || "http://3.6.15.101:5000/";
  const [loading, setLoading] = useState(false);
  const chatEndRef = useRef(null);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [loading]);

  const handleInputChange = (event) => {
    setUserInput(event.target.value);
  };
  const handleSliderChange = (event, newValue) => {
    setSliderValue(newValue);
  };

  const handleAnimation = () => {
    if (!isFocused) {
      gsap.to(".input-field", {
        height: 600,
        duration: 1,
        ease: Expo.easeInOut,
      });
      gsap.fromTo(
        ".prompt",
        {
          opacity: 1,
        },
        {
          opacity: 0,
          ease: Expo.easeInOut,
        }
      );
      gsap.fromTo(
        ".heading h1",
        {
          scale: 1,
        },
        {
          y: -160,
          scale: 0.5,
          yoyo: true,
          duration: 1,
          ease: Expo.easeInOut,
        }
      );
      gsap.fromTo(
        ".title",
        {
          opacity: 0,
        },
        {
          opacity: 1,
          zIndex: 1,
          delay: 0.5,
          ease: Expo.easeInOut,
        }
      );
    }
  };
  const handlePrompt = (promptt) => {
    setUserInput(promptt);
  };
  const handelRefresh = (location) => {
    location.reload();
  };
  const handleActionClick = (actionName) => {
    switch (actionName) {
      case "Copy":
        break;
      case "Save":
        downloadStory();
        break;
      case "Share":
        handleShare();
        break;
      default:
        break;
    }
  };
  const downloadStory = () => {
    if (!generatedStory) {
      return;
    }

    const blob = new Blob([generatedStory], { type: "text/plain" });
    const url = URL.createObjectURL(blob);

    const a = document.createElement("a");
    a.href = url;
    a.download = "generated-story.txt";
    a.style.display = "none";

    document.body.appendChild(a);
    a.click();

    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };
  const handleShare = async () => {
    if (navigator.share) {
      try {
        await navigator.share({
          title: "Generated Story",
          text: "Check out this generated story!",
          files: [
            new File([generatedStory], "generated-story.txt", {
              type: "text/plain",
            }),
          ],
        });
      } catch (error) {
        console.error("Error sharing:", error);
      }
    } else {
      console.error("Web Share API is not supported in this browser.");
    }
  };

  const generateStory = () => {
    setError(null);
    setGeneratedStory("");
    setIsFocused(true);
    handleAnimation();

    setTimeout(() => {
      setLoading(true);

      fetch(`${backendUrl}/generate-story`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ prompt: userInput, sliderValue: sliderValue }),
      })
        .then((response) => {
          if (!response.ok) {
            throw new Error("Network response was not ok");
          }
          return response.json();
        })
        .then((data) => {
          setGeneratedStory(data.story);
          setStoryId(data.storyId);
        })
        .catch((error) => {
          setError(error);
          console.error("Error:", error);
        })
        .finally(() => {
          setLoading(false);
        });
    }, 1000);
  };
  const actions = [
    { icon: <SaveIcon />, name: "Save" },
    { icon: <ShareIcon />, name: "Share" },
  ];
  const handleSliderSubmission = () => {
    fetch(`${backendUrl}/updatelike`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ sliderValue: sliderValue, storyId: storyId }),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        console.log(data); // Log the response for debugging
        setSliderShow(false);
        setLike(true);
      })
      .catch((error) => {
        console.error("Error updating like:", error);
        // Handle the error as needed
      });
  };

  const handleSend = async () => {
    if (!userInput.trim()) return;
    setChat((prev) => [...prev, { sender: "user", text: userInput }]);
    setLoading(true);
    setUserInput("");
    try {
      const response = await fetch(`${backendUrl}/generate-story`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ prompt: userInput, sliderValue: sliderValue })
      });
      if (!response.ok) throw new Error("Network response was not ok");
      const data = await response.json();
      setChat((prev) => [...prev, { sender: "bot", text: data.story }]);
    } catch (error) {
      setChat((prev) => [...prev, { sender: "bot", text: "Sorry, there was an error generating a response." }]);
    } finally {
      setLoading(false);
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  return (
    <div className="form-container flex flex-col items-center w-full h-full">
      <div className="heading left-[44%]">
        <h1>
          <span>Tarot</span>Reader
        </h1>
      </div>
      <div className="chat-area w-[60vw] h-[60vh] bg-[#2D2A4A] rounded-xl shadow-lg shadow-[#B8B8FF] p-6 overflow-y-auto my-8 flex flex-col" style={{ maxWidth: 700, minHeight: 400 }}>
        {chat.map((msg, idx) => (
          <div key={idx} className={`mb-4 flex ${msg.sender === "user" ? "justify-end" : "justify-start"}`}>
            <div className={`px-4 py-2 rounded-2xl max-w-[70%] ${msg.sender === "user" ? "bg-[#FFD700] text-[#2D2A4A]" : "bg-[#1E1E2F] text-[#F3F3F3]"}`}>
              {msg.text}
            </div>
          </div>
        ))}
        {loading && (
          <div className="mb-4 flex justify-start">
            <div className="px-4 py-2 rounded-2xl bg-[#1E1E2F] text-[#F3F3F3] opacity-70">Thinking...</div>
          </div>
        )}
        <div ref={chatEndRef} />
      </div>
      <div className="input-field w-[60vw] flex items-center" style={{ maxWidth: 700 }}>
        <input
          type="text"
          placeholder="Type your message and press Enter..."
          value={userInput}
          onChange={handleInputChange}
          onKeyDown={handleKeyDown}
          className="flex-1 bg-[#2D2A4A] text-[#F3F3F3] border-[#FFD700] border-2 rounded-xl px-4 py-3 outline-none"
        />
        <button className="submit-btn ml-2" onClick={handleSend} disabled={loading}>
          <i className="fa-solid fa-paper-plane"></i>
        </button>
      </div>
    </div>
  );
}
