import React, { useState } from "react";
import { gsap, Expo } from "gsap";
import Prompt from "./components/prompt";
import CircularProgress from "@mui/material/CircularProgress";
import SpeedDial from "@mui/material/SpeedDial";
import Box from "@mui/material/Box";
import SpeedDialIcon from "@mui/material/SpeedDialIcon";
import SpeedDialAction from "@mui/material/SpeedDialAction";
import SaveIcon from "@mui/icons-material/Save";
import ShareIcon from "@mui/icons-material/Share";
import CustomizedSlider from "./components/upvoteslider";
import Button from '@mui/material/Button';
import Leadercompo from "./components/leadercompo";

export default function Form() {
  const backendUrl = "http://localhost:5000" || "http://3.6.15.101:5000/";
  const [userInput, setUserInput] = useState("");
  const [generatedStory, setGeneratedStory] = useState("");
  const [error, setError] = useState(null);
  const [isFocused, setIsFocused] = useState(false);
  const [loading, setLoading] = useState(false);
  const [sliderValue, setSliderValue] = useState(20);
  const [slidershow, setSliderShow] = useState(true);
  const [like, setLike] = useState(false);
  const [storyId, setStoryId] = useState("");

  const handleInputChange = (event) => {
    setUserInput(event.target.value);
  };
  const handleSliderChange = (event, newValue) => {
    setSliderValue(newValue);
  };

  const handleAnimation = () => {
    if (!isFocused) {
      gsap.to(".input-field", {
        height: 600,
        duration: 1,
        ease: Expo.easeInOut,
      });
      gsap.fromTo(
        ".prompt",
        {
          opacity: 1,
        },
        {
          opacity: 0,
          ease: Expo.easeInOut,
        }
      );
      gsap.fromTo(
        ".heading h1",
        {
          scale: 1,
        },
        {
          y: -160,
          scale: 0.5,
          yoyo: true,
          duration: 1,
          ease: Expo.easeInOut,
        }
      );
      gsap.fromTo(
        ".title",
        {
          opacity: 0,
        },
        {
          opacity: 1,
          zIndex: 1,
          delay: 0.5,
          ease: Expo.easeInOut,
        }
      );
    }
  };
  const handlePrompt = (promptt) => {
    setUserInput(promptt);
  };
  const handelRefresh = (location) => {
    location.reload();
  };
  const handleActionClick = (actionName) => {
    switch (actionName) {
      case "Copy":
        break;
      case "Save":
        downloadStory();
        break;
      case "Share":
        handleShare();
        break;
      default:
        break;
    }
  };
  const downloadStory = () => {
    if (!generatedStory) {
      return;
    }

    const blob = new Blob([generatedStory], { type: "text/plain" });
    const url = URL.createObjectURL(blob);

    const a = document.createElement("a");
    a.href = url;
    a.download = "generated-story.txt";
    a.style.display = "none";

    document.body.appendChild(a);
    a.click();

    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };
  const handleShare = async () => {
    if (navigator.share) {
      try {
        await navigator.share({
          title: "Generated Story",
          text: "Check out this generated story!",
          files: [
            new File([generatedStory], "generated-story.txt", {
              type: "text/plain",
            }),
          ],
        });
      } catch (error) {
        console.error("Error sharing:", error);
      }
    } else {
      console.error("Web Share API is not supported in this browser.");
    }
  };

  const generateStory = () => {
    setError(null);
    setGeneratedStory("");
    setIsFocused(true);
    handleAnimation();

    setTimeout(() => {
      setLoading(true);

      fetch(`${backendUrl}/generate-story`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ prompt: userInput, sliderValue: sliderValue }),
      })
        .then((response) => {
          if (!response.ok) {
            throw new Error("Network response was not ok");
          }
          return response.json();
        })
        .then((data) => {
          setGeneratedStory(data.story);
          setStoryId(data.storyId);
        })
        .catch((error) => {
          setError(error);
          console.error("Error:", error);
        })
        .finally(() => {
          setLoading(false);
        });
    }, 1000);
  };
  const actions = [
    { icon: <SaveIcon />, name: "Save" },
    { icon: <ShareIcon />, name: "Share" },
  ];
  const handleSliderSubmission = () => {
    fetch(`${backendUrl}/updatelike`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ sliderValue: sliderValue, storyId: storyId }),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        console.log(data); // Log the response for debugging
        setSliderShow(false);
        setLike(true);
      })
      .catch((error) => {
        console.error("Error updating like:", error);
        // Handle the error as needed
      });
  };

  return (
    <div className="form-container flex w-full h-screen">
      {/* Sidebar: Chat History/Leaderboard */}
      <div className="sidebar bg-[#2D2A4A] shadow-lg shadow-[#B8B8FF] p-4 w-[25vw] min-w-[250px] max-w-[350px] h-full overflow-y-auto flex flex-col">
        <h2 className="text-[#FFD700] text-2xl font-bold mb-4 text-center">Chat History</h2>
        <Leadercompo />
      </div>
      {/* Main Chat Area */}
      <div className="flex-1 flex flex-col items-center justify-center h-full px-8">
        <div className="heading mb-2">
          <h1>
            <span>Tarot</span>Reader
          </h1>
        </div>
        <div className="welcome-message text-center font-semibold text-lg text-gray-800 mb-6">
          Welcome to TarotReader! Ask your question below.
        </div>
        <div className="grid grid-cols-2 gap-8 mb-8 w-full max-w-2xl">
          <div style={{ color: "green" }}>
            <Prompt
              icon="fa-solid fa-coins"
              text="Will my financial situation improve soon?"
              para="Money & Finance"
              onClick={() => handlePrompt("Will my financial situation improve soon?")}
            />
          </div>
          <div style={{ color: "purple" }}>
            <Prompt
              icon="fa-solid fa-briefcase"
              text="Am I on the right path in my career?"
              para="Career & Work"
              onClick={() => handlePrompt("Am I on the right path in my career?")}
            />
          </div>
          <div style={{ color: "brown" }}>
            <Prompt
              icon="fa-solid fa-heart"
              text="What is my relationship status?"
              para="Love & Relationships"
              onClick={() => handlePrompt("What is my relationship status?")}
            />
          </div>
          <div style={{ color: "hotpink" }}>
            <Prompt
              icon="fa-solid fa-heart-pulse"
              text="What is my health status?"
              para="Health & Wellness"
              onClick={() => handlePrompt("What is my health status?")}
            />
          </div>
        </div>
        <div className="input-field w-full max-w-2xl flex items-center mt-4 relative">
          <input
            type="text"
            placeholder="Start your Journey"
            value={userInput}
            onChange={handleInputChange}
            className="flex-1 bg-[#2D2A4A] text-[#F3F3F3] border-[#FFD700] border-2 rounded-xl px-4 py-3 outline-none"
          />
          <button className="submit-btn ml-2" onClick={generateStory}>
            <i className="fa-solid fa-paper-plane"></i>
          </button>
        </div>
        {loading && (
          <div className="loading-animation absolute left-1/2 top-1/2 z-10 w-25 h-1 0 transform -translate-x-1/2 -translate-y-1/2">
            <CircularProgress color="secondary" size="5vw" />
          </div>
        )}
        {generatedStory && !loading && (
          <div className="generated-story w-full max-w-2xl mt-8">
            <div className="story-frame z-10 border-[grey] border-[1px] rounded-l-3xl p-8 font-bold font-serif h-[30vh] overflow-y-scroll bg-[#1E1E2F] text-[#F3F3F3]">
              <p>{generatedStory}</p>
            </div>
          </div>
        )}
        {error && !loading && (
          <div className="error absolute z-[11] top-[20%] left-[23%] w-[58%]">
            <main className="grid min-h-full place-items-center bg-white px-6 py-24 sm:py-32 lg:px-8 ">
              <div className="text-center">
                <p className="text-base font-semibold text-[#57CACA]">404</p>
                <h1 className="mt-4 text-3xl font-bold tracking-tight text-gray-900 sm:text-5xl">
                  Page not found
                </h1>
                <p className="mt-6 text-base leading-7 text-gray-600">
                  Error In Story Genration Please Refresh.
                </p>
                <div className="mt-10 flex items-center justify-center gap-x-6">
                  <button
                    onClick={() => handelRefresh(window.location)}
                    className="rounded-md bg-[#57CACA] px-3.5 py-2.5 text-sm font-semibold text-white shadow-sm hover:bg-[#C37979] focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600 transition-all duration-[3000]"
                  >
                    Refresh
                  </button>
                </div>
              </div>
            </main>
          </div>
        )}
      </div>
    </div>
  );
}
