 export default function Prompt(props){
    return (
        

        <div className="p-6 max-w-sm mt-5 bg-[#2D2A4A] border-[#FFD700] border-2 rounded-xl shadow-lg shadow-[#B8B8FF] flex items-center space-x-4 sm:h-[50%] sm:w-[18vw] hover:bg-[#1E1E2F] transition font-mono prompt" onClick={props.onClick}>
            <div className="shrink-0 border-2 border-[#FFD700] w-8 h-8 rounded-lg text-center text-[#FFD700]">
                <i class={props.icon}></i>
            </div>    
            <div>
                <div className="font-bold text-[#F3F3F3] sm:mt-3 ">{props.text}</div>
                <p className="mb-5 text-[#B8B8FF]">
                    {props.para}
                </p>
            </div>
        </div>

    );
 }
